import React, { useRef, useEffect, useState } from "react";
import ListigPageComponent from "../ListingPageComponent/ListigPageComponent";
import axios from "axios";

function ListingPageContainer(props) {
  const listInnerRef = useRef();
  const [currPage, setCurrPage] = useState(1);
  const [prevPage, setPrevPage] = useState(0);
  const [userList, setUserList] = useState([]);
  const [lastList, setLastList] = useState(false);
  //  page=${currPage}&size=10`

  useEffect(() => {
    const fetchData = async () => {
      const response = await axios
        .get(`https://dummyjson.com/products?skip=5&limit=10`)

        .then((response) => console.log(response.data.data, "response data"));

      if (!response.data.data.length) {
        setLastList(true);
        return;
      }

      setPrevPage(currPage);
      setUserList([...userList, ...response.data.data]);
    };
    if (!lastList && prevPage !== currPage) {
      fetchData();
    }
  }, [currPage, lastList, prevPage, userList]);

  const onScroll = () => {
    if (listInnerRef.current) {
      const { scrollTop, scrollHeight, clientHeight } = listInnerRef.current;
      if (scrollTop + clientHeight === scrollHeight) {
        setCurrPage(currPage + 1);
      }
    }
  };

  return (
    <ListigPageComponent
      onScroll={onScroll}
      listInnerRef={listInnerRef}
      userList={userList}
    />
  );
}

export default ListingPageContainer;
